import { CommonModule } from '@angular/common';
import { Component } from '@angular/core';
import { RouterOutlet } from '@angular/router';
import { ButtonComponent } from './atoms/button/button.component';

@Component({
  selector: 'app-root',
  standalone: true,
  imports: [CommonModule, RouterOutlet, ButtonComponent],
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent {}

import { CommonModule } from '@angular/common';
import { Component, Input, } from '@angular/core';

/**
 construir una lista de clases CSS a partir de los argumentos pasados,
 */
const getStyles = (...args: string[]) => ["button", ...args].filter(Boolean);

@Component({
  selector: 'app-button',
  standalone: true,
  imports: [CommonModule],
  templateUrl: './button.component.html',
  styleUrls: ['./button.component.scss']
})
export class ButtonComponent {
  // Propiedades que serán requeridas en el componente
  @Input({ required: true }) label: string = 'Button';
  @Input({ required: true }) type: 'primary' | 'secondary' | 'tertiary' = 'primary';

  public get classes(): string[] {
    return getStyles(this.type);
  }
}

styleUrlsData.data:
.button {
  background-color: var(--primary_300);
  border: none;
  color: black;
  padding: 8px 16px;
  text-align: center;
  text-decoration: none;
  display: inline-block;
  font-size: 16px;
  margin: 4px 2px;
  cursor: pointer;
  resize: both;
  overflow: auto;
}

.secondary {
  background-color: var(--warning_600);
  color: white;
}

.tertiary {
  background-color: var(--info_600);
  color: white;
}

